import React from 'react'
import './ToggleDarkMode.css'
import Sun from '../sun-solid.svg'
import Moon from '../moon-solid.svg'


const ToggleDarkMode = () => {

  const toggle = (e) => {
    e.preventDefault();
    document.querySelector('#App').style.backgroundColor = 'white'
    document.querySelector('.t-button').style.right = '-50px'
  }

  return (
    <div className='t-darkmode'>
      <img src={Sun} alt="toggle" className='t-icon icon1'/>
      <img src={Moon} alt="toggle" className='t-icon icon2'/>
      <div className='t-button' onClick={toggle}></div>   
    </div>
  )
}

export default ToggleDarkMode
